CREATE OR REPLACE PROCEDURE inserir_inf_adic_prescr ( nr_prescricao_p bigint,			-- prescr_procedimento
 nr_seq_item_p bigint,			-- prescr_procedimento
 nr_seq_inf_adic_p bigint,			-- rep_inf_adic_exame
 nr_seq_item_inf_p bigint,			-- rep_inf_adic_item
 ds_valor_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pp_w	bigint;
nr_seq_ppi_w	bigint;
ie_tipo_campo_w	varchar(3);


BEGIN

begin
select	nr_sequencia
into STRICT	nr_seq_pp_w
from	rep_inf_adic_pp
where	nr_prescricao	=	nr_prescricao_p
and	nr_seq_item	=	nr_seq_item_p
and	nr_seq_inf_adic	=	nr_seq_inf_adic_p;
exception
	when others then
		nr_seq_pp_w	:= 0;
end;


/* o procedimento não possui informação adicional (inserir - rep_inf_adic_pp) */

if (nr_seq_pp_w = 0) then

	select	nextval('rep_inf_adic_pp_seq')
	into STRICT	nr_seq_pp_w
	;

	insert	into rep_inf_adic_pp(
		nr_sequencia,
		nr_prescricao,
		nr_seq_item,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_inf_adic)
	values (
		nr_seq_pp_w,
		nr_prescricao_p,
		nr_seq_item_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_inf_adic_p);

	commit;

end if;

if (nr_seq_pp_w > 0) then

	begin
	select	a.nr_sequencia
	into STRICT	nr_seq_ppi_w
	from	rep_inf_adic_ppi a
	where	a.nr_seq_inf_adic	=	nr_seq_pp_w
	and	a.nr_seq_item_inf_adic	=	nr_seq_item_inf_p;
	exception
		when others then
			nr_seq_ppi_w	:= 0;
	end;

	select	ie_tipo_campo
	into STRICT	ie_tipo_campo_w
	from	rep_inf_adic_item
	where	nr_sequencia		=	nr_seq_item_inf_p;

	if (nr_seq_ppi_w = 0) then
		/* inserir o item (insert) */

		insert	into rep_inf_adic_ppi(
			nr_sequencia,
			nr_seq_inf_adic,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_item_inf_adic,
			nr_inf_adic,
			ds_inf_adic,
			dt_inf_adic)
		values (
			nextval('rep_inf_adic_ppi_seq'),
			nr_seq_pp_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_item_inf_p,
			CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
			CASE WHEN ie_tipo_campo_w='A' THEN ds_valor_p  ELSE null END ,
			CASE WHEN ie_tipo_campo_w='D' THEN to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss')  ELSE null END );
		commit;
	else
		/* atualizar o item (update) */

		update	rep_inf_adic_ppi
		set	nr_inf_adic	=	CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
			ds_inf_adic	=	CASE WHEN ie_tipo_campo_w='A' THEN ds_valor_p  ELSE null END ,
			dt_inf_adic	=	CASE WHEN ie_tipo_campo_w='D' THEN to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss')  ELSE null END ,
			nm_usuario	=	nm_usuario_p,
			dt_atualizacao	=	clock_timestamp()
		where	nr_sequencia	=	nr_seq_ppi_w;
		commit;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
