CREATE OR REPLACE PROCEDURE inserir_item_regra_pepo (nr_seq_item_pepo_p bigint, nr_seq_pasta_pepo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_existe_regra_w 	varchar(1) := 'N';
								

BEGIN

if (nr_seq_item_pepo_p IS NOT NULL AND nr_seq_item_pepo_p::text <> '') then

	select 	coalesce(max('S'), 'N')
	into STRICT 	ie_existe_regra_w
	from 	checklist_cirurgia_regra
	where 	nr_seq_item_pepo = nr_seq_item_pepo_p
	and 	nr_seq_pasta_pepo = nr_seq_pasta_pepo_p;
	
	if (ie_existe_regra_w = 'N') then
		insert into checklist_cirurgia_regra(
			nr_sequencia,
			nr_seq_item_pepo,
			nr_seq_pasta_pepo,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec)
		values (
			nextval('checklist_cirurgia_regra_seq'),
			nr_seq_item_pepo_p,
			nr_seq_pasta_pepo_p,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp());

		commit;
	end if;	

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
