SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_itens_conta_barras ( nm_usuario_p text, nr_seq_interno_p bigint, nr_seq_protocolo_p bigint, nr_documento_p bigint, nr_Seq_item_p bigint, nr_Sequencia_p bigint) AS $body$
DECLARE


nr_seq_tipo_item_w	bigint;
ie_tipo_documento_w	varchar(1);
ie_gerardescperiodoconta_w 	varchar(1);
ie_formadocconta_w		varchar(1);
ds_documento_w          varchar(100);
cd_estabelecimento_w  estabelecimento.cd_estabelecimento%type;
dt_periodo_inicial_w  conta_paciente.dt_periodo_inicial%type;
dt_periodo_final_w	  conta_paciente.dt_periodo_final%type;



BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

ie_tipo_documento_w := obter_param_usuario(290, 106, obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_tipo_documento_w);
ie_formadocconta_w := obter_param_usuario(290, 26, obter_perfil_ativo, obter_usuario_ativo, cd_estabelecimento_w, ie_formadocconta_w);
ie_gerardescperiodoconta_w := obter_param_usuario(290, 92, obter_perfil_ativo, obter_usuario_ativo, cd_estabelecimento_w, ie_gerardescperiodoconta_w);

if (ie_tipo_documento_w = 'S') then
	select	nr_seq_tipo_doc_item
	into STRICT	nr_seq_tipo_item_w
	from	protocolo_documento
	where	nr_sequencia = nr_sequencia_p;
end if;

if ((ie_formadocconta_w = 'C') and (ie_gerardescperiodoconta_w = 'S') and (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '')) then

    	begin
		    select  	dt_periodo_inicial,
				dt_periodo_final
		    into STRICT 	dt_periodo_inicial_w,
				dt_periodo_final_w
		    from		conta_paciente
		    where 	nr_interno_conta = nr_seq_interno_p;
		    exception
		    when others then
		   	dt_periodo_inicial_w	:= null;
			dt_periodo_final_w	:= null;
		   end;
		   	--Período da conta é de #@DT_PERIODO_INICIAL#@ até #@DT_PERIODO_FINAL#@
		   ds_documento_w := wheb_mensagem_pck.get_texto(882113,'DT_PERIODO_INICIAL=' || dt_periodo_inicial_w ||';DT_PERIODO_FINAL='|| dt_periodo_final_w);
		end if;

insert into protocolo_doc_item(
			dt_atualizacao,
			dt_inclusao_item,
			nm_usuario,
			nr_doc_item,
			nr_documento,
			nr_seq_interno,
			nr_seq_item,
			nr_sequencia,
			nr_seq_tipo_item,
			ds_documento)
values (			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_protocolo_p,
			nr_documento_p,
			nr_Seq_interno_p,
			nr_Seq_item_p,
			nr_sequencia_p,
			nr_seq_tipo_item_w,
			ds_documento_w
			);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
