CREATE OR REPLACE PROCEDURE inserir_itens_grupo_exame ( nr_seq_agenda_p bigint, nr_seq_grupo_exame_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w	bigint;
nr_seq_agenda_w			bigint	:= 0;
			
C01 CURSOR FOR 
	SELECT	cd_procedimento, 
			ie_origem_proced, 
			nr_proc_interno 
	from	med_exame_padrao 
	where	nr_seq_grupo	= nr_seq_grupo_exame_p 
	order by 1;
			

BEGIN 
 
select	coalesce(max(nr_seq_Agenda),0) 
into STRICT	nr_seq_agenda_w 
from	agenda_paciente_proc 
where	nr_sequencia	= nr_seq_Agenda_p;
 
open C01;
loop 
fetch C01 into	 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') and 
		((coalesce(cd_procedimento_w::text, '') = '') or (coalesce(ie_origem_proced_w::text, '') = '')) then		 
		SELECT * FROM obter_proc_tab_interno_conv(nr_seq_proc_interno_w, cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, null, null, cd_procedimento_w, ie_origem_proced_w, null, null, null, null, null, null, null, null, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
	end if;
	 
	nr_seq_agenda_w	:= nr_seq_agenda_w + 1;
	 
	if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') and (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') then 
		insert into agenda_paciente_proc(nr_sequencia, 
				nr_seq_agenda, 
				dt_atualizacao, 
				nm_usuario, 
				cd_procedimento, 
				ie_origem_proced, 
				nr_seq_proc_interno) 
		values (nr_seq_Agenda_p, 
				nr_seq_agenda_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				cd_procedimento_w, 
				ie_origem_proced_w, 
				nr_seq_proc_interno_w);
	end if;
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
