CREATE OR REPLACE PROCEDURE inserir_jutificativa_mat_dose ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_material_p bigint, cd_material_p bigint, qt_dose_atual_p bigint, cd_unidade_medida_p text, qt_dose_limite_p bigint, cd_unid_med_limite_p text, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

if (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then

	select	count(*)
	into STRICT	qt_registros_w
	from	prescr_mat_just_dose
	where	cd_material = cd_material_p
	and		nr_atendimento_p = nr_atendimento_p
	and		nr_prescricao_p = nr_prescricao_p
	and		nr_seq_material = nr_seq_material_p
	and		nm_usuario = nm_usuario_p
	and		upper(replace(ds_justificativa,' ','')) = upper(replace(ds_justificativa_p,' ',''));

	if (qt_registros_w = 0) then

		insert into prescr_mat_just_dose(
							nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_atendimento,
							nr_prescricao,
							cd_material,
							qt_dose_atual,
							cd_unidade_medida_dose,
							qt_dose_limite,
							cd_unidade_medida_limite,
							nr_seq_material,
							ds_justificativa)
						values (
							nextval('prescr_mat_just_dose_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_atendimento_p,
							nr_prescricao_p,
							cd_material_p,
							qt_dose_atual_p,
							cd_unidade_medida_p,
							qt_dose_limite_p,
							cd_unid_med_limite_p,
							nr_seq_material_p,
							ds_justificativa_p);

		commit;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
