CREATE OR REPLACE PROCEDURE inserir_laudo_grupo_questao ( nr_seq_grupo_p bigint, nr_seq_laudo_p bigint, nm_usuario_p text, nr_seq_peca_p bigint DEFAULT 0) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_item_grupo_w	bigint;
nr_seq_questao_w	bigint;
ds_texto_padrao_w	varchar(2000);

c01 CURSOR FOR
SELECT	nr_sequencia,
	ds_texto_padrao
FROM	item_questao_laudo
WHERE	nr_seq_grupo	= nr_seq_grupo_p;


BEGIN

SELECT	nextval('laudo_grupo_questao_seq')
INTO STRICT	nr_sequencia_w
;

INSERT INTO laudo_grupo_questao(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_laudo,
	nr_seq_grupo_questao,
	nr_seq_peca)
VALUES (nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_laudo_p,
	nr_seq_grupo_p,
	coalesce(nr_seq_peca_p,0));

OPEN C01;
LOOP
FETCH C01 INTO
	nr_seq_item_grupo_w,
	ds_texto_padrao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	SELECT	nextval('laudo_questao_item_seq')
	INTO STRICT	nr_seq_questao_w
	;

	INSERT INTO laudo_questao_item(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_laudo_grupo,
		nr_seq_item_questao,
		ds_texto)
	VALUES (nr_seq_questao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		nr_seq_item_grupo_w,
		ds_texto_padrao_w);

END LOOP;
CLOSE C01;

COMMIT;

END;
$body$
