CREATE OR REPLACE PROCEDURE inserir_lista_contato ( nm_usuario_base_p text, nm_usuario_destino_p text, nm_usuario_nrec_p text) AS $body$
DECLARE


ds_apelido_w		varchar(50);
nm_usuario_amigo_w	varchar(15);
nr_sequencia_w		bigint;



c01 CURSOR FOR
SELECT	a.ds_apelido,
	a.nm_usuario_amigo
from	usuario_amigo a,
	usuario b
where	a.nm_usuario_base = b.nm_usuario
and	b.nm_usuario = nm_usuario_base_p
and not exists (select	1
		from	usuario_amigo x
		where	x.nm_usuario_base = nm_usuario_destino_p
		and	x.nm_usuario_amigo = a.nm_usuario_amigo);



BEGIN

open c01;
loop
fetch c01 into
	ds_apelido_w,
	nm_usuario_amigo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('usuario_amigo_seq')
	into STRICT	nr_sequencia_w
	;


	insert into usuario_amigo(
		nr_sequencia,
		nm_usuario_base,
		nm_usuario_amigo,
		dt_atualizacao,
		nm_usuario,
		ds_apelido,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (	nr_sequencia_w,
		nm_usuario_destino_p,
		nm_usuario_amigo_w,
		clock_timestamp(),
		nm_usuario_nrec_p,
		ds_apelido_w,
		clock_timestamp(),
		nm_usuario_nrec_p);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
