CREATE OR REPLACE PROCEDURE inserir_list_opme_grupo_agenda ( nr_seq_agenda_p bigint, nm_usuario_p text, ie_opme_grupo_p text, ds_lista_opme_grupo_p text) AS $body$
DECLARE

/*ie_opme_grupo_p - O : OPME; G - Grupo de OPME*/
 
ds_lista_opme_w		varchar(10000);
qt_tam_lista_w		bigint;
ie_pos_virgula_w 	integer;
nr_seq_opme_grupo_w	bigint;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_opme_grupo_p IS NOT NULL AND ie_opme_grupo_p::text <> '') and (ds_lista_opme_grupo_p IS NOT NULL AND ds_lista_opme_grupo_p::text <> '') then 
	begin 
	ds_lista_opme_w := ds_lista_opme_grupo_p;
	 
	while(ds_lista_opme_w IS NOT NULL AND ds_lista_opme_w::text <> '') loop 
		begin 
		qt_tam_lista_w		:= length(ds_lista_opme_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_opme_w);
 
		if (ie_pos_virgula_w <> 0) then 
			begin 
			nr_seq_opme_grupo_w	:= substr(ds_lista_opme_w,1,(ie_pos_virgula_w - 1));
			ds_lista_opme_w	:= substr(ds_lista_opme_w,(ie_pos_virgula_w + 1),qt_tam_lista_w);
			end;
		else 
			begin 
			nr_seq_opme_grupo_w	:= (ds_lista_opme_w)::numeric;
			ds_lista_opme_w	:= null;
			end;
		end if;
 
		if (nr_seq_opme_grupo_w IS NOT NULL AND nr_seq_opme_grupo_w::text <> '') and (nr_seq_opme_grupo_w > 0) then 
			begin 
			if (ie_opme_grupo_p = 'G') then 
				begin 
				CALL inserir_opme_grupo_agenda( 
					nr_seq_agenda_p, 
					null, 
					nr_seq_opme_grupo_w, 
					nm_usuario_p);
				end;
			elsif (ie_opme_grupo_p = 'O') then 
				begin 
				CALL inserir_opme_grupo_agenda( 
					nr_seq_agenda_p, 
					nr_seq_opme_grupo_w, 
					null,						 
					nm_usuario_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
end;
$body$
