CREATE OR REPLACE PROCEDURE inserir_lote_audit_hist ( cd_autorizacao_p text, nr_interno_conta_p bigint, vl_saldo_guia_p bigint, nr_seq_retorno_p bigint, vl_saldo_p text, nm_usuario_p text, ie_gerar_itens_p text, nr_seq_lote_hist_p bigint) AS $body$
DECLARE


nr_seq_retorno_w	bigint;
nr_sequencia_w 		bigint;
ie_guia_sem_saldo_w 	varchar(1);
ie_exibir_aprovacao_w	varchar(1);
dt_fechamento_w		lote_auditoria.dt_fechamento%type;
dt_aprovacao_w		lote_audit_hist.dt_aprovacao%type;


BEGIN

select 	CASE WHEN nr_seq_retorno_p=0 THEN null  ELSE nr_seq_retorno_p END
into STRICT 	nr_seq_retorno_w
;

if 	(nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '' AND cd_autorizacao_p IS NOT NULL AND cd_autorizacao_p::text <> '') then

	select	a.dt_fechamento,
		b.dt_aprovacao
	into STRICT	dt_fechamento_w,
		dt_aprovacao_w
	from	lote_auditoria a,
		lote_audit_hist b
	where	a.nr_sequencia = b.nr_seq_lote_audit
	and	b.nr_sequencia = nr_seq_lote_hist_p;

	ie_exibir_aprovacao_w := obter_param_usuario(69, 42, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_exibir_aprovacao_w);

	if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(131536);
	elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') and (ie_exibir_aprovacao_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(203224);
	end if;

	if (vl_saldo_p = 0)then
		ie_guia_sem_saldo_w := 'S';
	else
		ie_guia_sem_saldo_w := 'N';
	end if;

	select	nextval('lote_audit_hist_guia_seq')
	into STRICT	nr_sequencia_w
	;

	insert into lote_audit_hist_guia(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_interno_conta,
		cd_autorizacao,
		nr_seq_lote_hist,
		nr_seq_retorno,
		vl_saldo_guia,
		ie_guia_sem_saldo,
		dt_baixa_glosa,
		ds_observacao)
	values (nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		nr_interno_conta_p,
		cd_autorizacao_p,
		nr_seq_lote_hist_p,
		nr_seq_retorno_w,
		vl_saldo_guia_p,
		ie_guia_sem_saldo_w,
		null,
		null);

	if (ie_gerar_itens_p = 'S')then
		CALL gerar_lote_audit_hist_item(nr_seq_retorno_p,null,nr_sequencia_w,nm_usuario_p);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
