CREATE OR REPLACE PROCEDURE inserir_macro_medicamento (nr_sequencia_p bigint, ds_macro_p text, nm_usuario_p text) AS $body$
DECLARE


ds_texto_w	varchar(2000);


BEGIN

select	ds_texto_padrao_receita
into STRICT	ds_texto_w
from	fa_medic_farmacia_amb
where	nr_sequencia = nr_sequencia_p;

ds_texto_w := ds_texto_w || ' ' || ds_macro_p;

update	fa_medic_farmacia_amb
set ds_texto_padrao_receita = ds_texto_w
where nr_sequencia = nr_sequencia_p;

commit;

end;
