SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_mat_autor_cir_barras (cd_material_p bigint, qt_material_p bigint, nr_sequencia_autor_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_mat_autor_w	bigint;					
					

BEGIN

if (qt_material_p IS NOT NULL AND qt_material_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_mat_autor_w
	from	material_autor_cirurgia
	where	nr_seq_autorizacao	= nr_sequencia_autor_p
	and	cd_material		= cd_material_p;

	if (coalesce(nr_seq_mat_autor_w::text, '') = '') then
		
		insert into material_autor_cirurgia(dt_atualizacao,
			nm_usuario,
			nr_seq_autorizacao,
			qt_material,
			vl_material,
			nr_sequencia,
			vl_unitario_material,
			ie_aprovacao,
			cd_material)
		values (
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_autor_p,
			qt_material_p,
			0,
			nextval('material_autor_cirurgia_seq'),
			0,
			'N',
			cd_material_p);
	else
		update	material_autor_cirurgia
		set	qt_material	= qt_material + qt_material_p,
            nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp()
		where	nr_sequencia	= nr_seq_mat_autor_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
