CREATE OR REPLACE PROCEDURE inserir_mat_med_vacina ( nr_seq_vacina_p bigint, cd_material_p bigint, qt_material_p bigint, cd_unidade_medida_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_opcao_p text, nr_seq_lote_p bigint default null) AS $body$
DECLARE


/*
ie_opcao_p
I - Inserir
D - Devolver
*/
					
qt_material_w		double precision;
qt_mat_novo_w		double precision;
nr_atendimento_w	bigint;	
ie_atualizar_conta_w	varchar(1);

BEGIN

ie_atualizar_conta_w 	:= coalesce(obter_valor_param_usuario(903,17,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_p),'N');

if (nr_seq_vacina_p IS NOT NULL AND nr_seq_vacina_p::text <> '') then
	
	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	paciente_vacina
	where	nr_sequencia	= nr_seq_vacina_p;

	select	count(*)
	into STRICT	qt_material_w
	from	vacina_paciente_material
	where	cd_material = cd_material_p
	and	cd_unidade_medida = cd_unidade_medida_p
	and	nr_seq_vacina = nr_seq_vacina_p;

	if (ie_opcao_p = 'D') then
		qt_mat_novo_w := qt_material_p * -1;
	else
		qt_mat_novo_w := qt_material_p;
	end if;
	
	if (ie_atualizar_conta_w = 'S') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
 		CALL baixar_mat_barras_imunizacoes(nr_seq_vacina_p, nr_atendimento_w, cd_material_p, qt_mat_novo_w, nm_usuario_p, nr_seq_lote_p);
	end if;
	
	if (qt_material_w > 0) then
		
		update	vacina_paciente_material
		set	qt_material = qt_material + qt_mat_novo_w
		where	cd_material = cd_material_p
		and	cd_unidade_medida = cd_unidade_medida_p
		and	nr_seq_vacina = nr_seq_vacina_p;
		
		delete	FROM vacina_paciente_material
		where	cd_material = cd_material_p
		and	cd_unidade_medida = cd_unidade_medida_p
		and	nr_seq_vacina = nr_seq_vacina_p
		and	qt_material = 0;
		
	else
	
		insert into vacina_paciente_material(	
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_material,
			qt_material,
			cd_unidade_medida,
			nr_seq_vacina,
			nr_seq_lote)
		values (	nextval('vacina_paciente_material_seq'),
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_material_p,
			qt_mat_novo_w,
			cd_unidade_medida_p,
			nr_seq_vacina_p,
			CASE WHEN nr_seq_lote_p=0 THEN null  ELSE nr_seq_lote_p END );
		
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_mat_med_vacina ( nr_seq_vacina_p bigint, cd_material_p bigint, qt_material_p bigint, cd_unidade_medida_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_opcao_p text, nr_seq_lote_p bigint default null) FROM PUBLIC;

