CREATE OR REPLACE PROCEDURE inserir_medicamento_hist_saude ( nr_prescricao_p bigint, nr_atendimento_p bigint, cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_p text, cd_intervalo_p text, ie_via_aplicacao_p text, nm_usuario_p text, ie_medic_paciente_p text) AS $body$
DECLARE

 
nr_sequencia_w			prescr_material.nr_sequencia%type;
nr_agrupamento_w		prescr_material.nr_agrupamento%type;
qt_unitaria_w			prescr_material.qt_unitaria%type;
nr_horas_validade_w		prescr_medica.nr_horas_validade%type;
ds_horarios_w			varchar(255):= '';
ds_horarios_ww			varchar(7000):= '';
nr_intervalo_w			bigint := 0;
dt_prescricao_w			timestamp;
dt_inicio_prescr_w		timestamp;
dt_primeiro_horario_w  timestamp;
ds_medic_nao_padrao_w 	varchar(255);


BEGIN 
 
select max(coalesce(dt_prescricao,clock_timestamp())), 
	max(dt_inicio_prescr), 
	max(coalesce(nr_horas_validade,24)), 
	max(dt_primeiro_horario) 
into STRICT	dt_prescricao_w, 
	dt_inicio_prescr_w, 
	nr_horas_validade_w, 
	dt_primeiro_horario_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p 
and	nr_atendimento = nr_atendimento_p;
 
 
select	coalesce(max(nr_sequencia),0) + 1, 
	coalesce(max(nr_agrupamento),0) + 1, 
	obter_conversao_unid_med_cons(cd_material_p,cd_unidade_medida_p, qt_dose_p) 
into STRICT	nr_sequencia_w, 
	nr_agrupamento_w, 
	qt_unitaria_w 
from	prescr_material 
where	nr_prescricao	= nr_prescricao_p;
 
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then 
	SELECT * FROM Calcular_Horario_Prescricao(	 
			nr_prescricao_p, cd_intervalo_p, dt_primeiro_horario_w, dt_inicio_prescr_w, nr_horas_validade_w, cd_material_p, 0, 0, nr_intervalo_w, ds_horarios_w, ds_horarios_ww, 'N', null) INTO STRICT nr_intervalo_w, ds_horarios_w, ds_horarios_ww;
end if;
 
if ((Wheb_assist_pck.obterParametroFuncao(924,1094) = 'S') and (ie_medic_paciente_p = 'S')) then	 
	ds_medic_nao_padrao_w := substr(obter_desc_material(cd_material_p), 1, 255);
end if;
		 
insert into prescr_material( 
		nr_sequencia, 
		nr_agrupamento, 
		cd_material, 
		cd_intervalo, 
		cd_unidade_medida, 
		cd_unidade_medida_dose, 
		ie_via_aplicacao, 
		ie_agrupador, 
		nr_prescricao, 
		qt_dose, 
		dt_atualizacao, 
		dt_atualizacao_nrec, 
		nm_usuario, 
		nm_usuario_nrec, 
		ie_origem_inf, 
		qt_unitaria, 
		qt_material, 
		ds_horarios, 
		ie_medicacao_paciente, 
		ds_medic_nao_padrao) 
		values ( 
		nr_sequencia_w, 
		nr_agrupamento_w, 
		cd_material_p, 
		cd_intervalo_p, 
		cd_unidade_medida_p, 
		cd_unidade_medida_p, 
		ie_via_aplicacao_p, 
		1, 
		nr_prescricao_p, 
		qt_dose_p, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		nm_usuario_p, 
		1, 
		qt_unitaria_w, 
		1, 
		ds_horarios_w || ds_horarios_ww, 
		ie_medic_paciente_p, 
		ds_medic_nao_padrao_w);
 
	commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
