CREATE OR REPLACE PROCEDURE inserir_medicamento_prescricao ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_agrupamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	integer;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_material
where	nr_prescricao		= nr_prescricao_p
and	ie_agrupador		= 1
and	nr_agrupamento		> nr_agrupamento_p
and	coalesce(nr_sequencia_diluicao::text, '') = ''
and	coalesce(nr_sequencia_proc::text, '') = ''
and	coalesce(nr_sequencia_solucao::text, '') = ''
and	coalesce(nr_sequencia_dieta::text, '') = '';


BEGIN
open c01;
loop
fetch c01 into nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	prescr_material
	set	nr_agrupamento	= nr_agrupamento + 1
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_w;

	if (nr_sequencia_w = nr_sequencia_p) then
		begin
		exit;
		end;
	end if;
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
