CREATE OR REPLACE PROCEDURE inserir_medic_doador_cadaver ( nr_seq_doador_p bigint, ds_lista_medic_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_medic_w			bigint;


BEGIN

lista_informacao_w	:= ds_lista_medic_p;

while((lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or ie_contador_w > 200) loop
	begin

	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	/* Obter a sequÃªncia lida */

	if (ie_pos_virgula_w <> 0) then
		nr_seq_medic_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	insert into tx_doador_cadaver_medic(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material,
		qt_dose,
		cd_unidade_medida,
		cd_intervalo,
		ie_via_aplicacao,
		nr_seq_doador
	)
	SELECT	nextval('medic_uso_continuo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material,
		qt_dose,
		cd_unidade_medida,
		cd_intervalo,
		ie_via_aplicacao,
		nr_seq_doador_p
	from	tx_medic_padroes_cadaver
	where	nr_sequencia	= nr_seq_medic_w;

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
