SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_medic_uso_padrao ( cd_pessoa_fisica_p text, nr_seq_receptor_p bigint, ds_lista_medic_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_medic_w			bigint;


BEGIN

lista_informacao_w	:= ds_lista_medic_p;

while((lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or ie_contador_w > 200) loop
	begin
	
	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	/* Obter a sequencia lida */

	if (ie_pos_virgula_w <> 0) then
		nr_seq_medic_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	insert into medic_uso_continuo(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		dt_inicio,
		cd_material,
		qt_dose,
		cd_unidade_medida,
		nr_dias_uso,
		ie_uso_continuo,
		ie_laudo_lme,
		cd_cid_principal,
		cd_cid_secundario,
		cd_intervalo,
		ie_via_aplicacao,
		ds_justificativa,
		ds_observacao,
		ie_utiliza_dialise
	)
	SELECT	nextval('medic_uso_continuo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_p,
		clock_timestamp(),
		cd_material,
		qt_dose,
		cd_unidade_medida,
		nr_dias_uso,
		ie_uso_continuo,
		ie_laudo_lme,
		cd_cid_principal,
		cd_cid_secundario,
		cd_intervalo,
		ie_via_aplicacao,
		ds_justificativa,
		ds_observacao,
		ie_utiliza_dialise		
	from	medic_uso_continuo_padrao
	where	nr_sequencia	= nr_seq_medic_w;
	
	ie_contador_w	:= ie_contador_w + 1;
	
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
