SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_metodo_nf_cred (nr_seq_nf_credito_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_tipo_recebimento_w		tipo_recebimento.cd_tipo_recebimento%type;
nr_seq_nf_orig_w   		nf_credito.nr_seq_nf_orig%TYPE;
ie_tipo_relacao_w               nf_credito.ie_tipo_relacao%TYPE;
qtd_w				bigint;

c05 CURSOR FOR
SELECT  cd_tipo_recebimento
FROM	fis_metodo_pagamento
WHERE   nr_seq_nota = nr_seq_nf_orig_w;


BEGIN

select count(*)
into STRICT   qtd_w
from   fis_metodo_pagamento
where  nr_seq_nota = nr_sequencia_p;


if (qtd_w = 0) then

	SELECT	max(ie_tipo_relacao),
		max(nr_seq_nf_orig)
	INTO STRICT	ie_tipo_relacao_w,
		nr_seq_nf_orig_w
	FROM	nf_credito
	WHERE	nr_sequencia = nr_seq_nf_credito_p;

	IF ((coalesce(ie_tipo_relacao_w::text, '') = '') OR (ie_tipo_relacao_w <> '07')) THEN

		OPEN	
		c05;
		LOOP
		FETCH c05 INTO
		cd_tipo_recebimento_w;
		EXIT WHEN NOT FOUND; /* apply on c05 */
		BEGIN

		CALL inserir_metodo_pagamento(cd_tipo_recebimento_w,nr_sequencia_p,nm_usuario_p);

		END;
		END LOOP;

	CLOSE c05;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
