CREATE OR REPLACE PROCEDURE inserir_modulos_tasy_cur_pf ( nr_seq_pf_proj_tasy_p bigint, ds_lista_modulos_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_modulos_w	varchar(4000);
nr_seq_modulo_w		bigint;
nr_pos_virgula_w	bigint;


BEGIN
if (nr_seq_pf_proj_tasy_p IS NOT NULL AND nr_seq_pf_proj_tasy_p::text <> '') and (ds_lista_modulos_p IS NOT NULL AND ds_lista_modulos_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_modulos_w := ds_lista_modulos_p;

	while(ds_lista_modulos_w IS NOT NULL AND ds_lista_modulos_w::text <> '') loop
		begin

		nr_pos_virgula_w	:= position(',' in ds_lista_modulos_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_modulo_w		:= (substr(ds_lista_modulos_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_modulos_w	:= substr(ds_lista_modulos_w,nr_pos_virgula_w+1,length(ds_lista_modulos_w));
			end;
		else
			begin
			nr_seq_modulo_w		:= (ds_lista_modulos_w)::numeric;
			ds_lista_modulos_w	:= null;
			end;
		end if;

		if (coalesce(nr_seq_modulo_w,0) > 0) then
			begin
			insert into pf_projeto_tasy_modulo(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				nr_seq_pf_proj_tasy,
				nr_seq_modulo)
			values (
				nextval('pf_projeto_tasy_modulo_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_pf_proj_tasy_p,
				nr_seq_modulo_w);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
