CREATE OR REPLACE PROCEDURE inserir_motivo_manute_leito ( nm_usuario_p text, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_setor_manutencao_p bigint, cd_motivo_manutencao_p bigint, ds_observacao_p text) AS $body$
DECLARE


nr_seq_interno_w	bigint;
nr_sequencia_w		bigint;
nr_seq_hist_w		bigint;
ie_situacao_w		varchar(1);
ie_temporario_w		varchar(1);


BEGIN


if (cd_setor_manutencao_p IS NOT NULL AND cd_setor_manutencao_p::text <> '') and (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '') and (cd_unidade_compl_p IS NOT NULL AND cd_unidade_compl_p::text <> '') then

	update	unidade_atendimento
	set	cd_motivo_manutencao = CASE WHEN cd_motivo_manutencao_p=0 THEN null  ELSE cd_motivo_manutencao_p END ,
		dt_atualizacao	     = clock_timestamp(),
		nm_usuario	     = nm_usuario_p
	where	cd_unidade_basica    = cd_unidade_basica_p
	and	cd_unidade_compl     = cd_unidade_compl_p
	and 	cd_setor_atendimento = cd_setor_manutencao_p;

	select	nr_seq_interno,
		coalesce(ie_situacao,'A'),
		coalesce(ie_temporario,'N')
	into STRICT	nr_seq_interno_w,
		ie_situacao_w,
		ie_temporario_w
	from	unidade_atendimento
	where	cd_unidade_basica    = cd_unidade_basica_p
	and	cd_unidade_compl     = cd_unidade_compl_p
	and 	cd_setor_atendimento = cd_setor_manutencao_p;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_hist_w
	from   unidade_atend_hist
	where nr_seq_unidade = nr_seq_interno_w;

	if (nr_seq_hist_w > 0) then

		update	unidade_atend_hist
		set	dt_historico         = clock_timestamp(),
			dt_atualizacao       = clock_timestamp(),
			nm_usuario           = nm_usuario_p,
			ie_situacao          = ie_situacao_w,
			ie_status_unidade    =  'E',
			cd_motivo_manutencao = CASE WHEN cd_motivo_manutencao_p=0 THEN null  ELSE cd_motivo_manutencao_p END ,
			ie_temporario        = ie_temporario_w,
			ds_observacao        = substr(ds_observacao_p,1,255)
		where	nr_sequencia	     = nr_seq_hist_w
		and	nr_seq_unidade       = nr_seq_interno_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
