CREATE OR REPLACE PROCEDURE inserir_motivo_permanencia ( nm_usuario_p text, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_seq_motivo_perm_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;

BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from 	atend_paciente_unidade
where	nr_atendimento       = nr_atendimento_p
and 	cd_setor_atendimento = cd_setor_atendimento_p
and 	cd_unidade_basica    = cd_unidade_basica_p
and 	cd_unidade_compl     = cd_unidade_compl_p;

if (nr_sequencia_w > 0) then

	update	atend_paciente_unidade
	set	nr_seq_motivo_perm   = nr_seq_motivo_perm_p,
		nm_usuario           = nm_usuario_p,
		dt_atualizacao       = clock_timestamp()
	where	nr_sequencia   = nr_sequencia_w
	and	nr_atendimento = nr_atendimento_p;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
