CREATE OR REPLACE PROCEDURE inserir_motivo_recoleta_js ( nr_seq_motivo_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_material_exame_p text, ds_observacao_p text, ie_coleta_exame_p text, nm_usuario_p text) AS $body$
DECLARE



ie_amostra_recoleta_w		lab_parametro.ie_amostra_recoleta%type;


BEGIN

ie_amostra_recoleta_w := 'N';

select	max(a.ie_amostra_recoleta)
into STRICT	ie_amostra_recoleta_w
from	lab_parametro a,
	prescr_medica b
where 	a.cd_estabelecimento = b.cd_estabelecimento
and	b.nr_prescricao = nr_prescricao_p;

if (ie_coleta_exame_p = 'S') then

	update 	prescr_procedimento
	set 	nr_seq_recoleta = nr_seq_motivo_p,
		nm_usuario 	= nm_usuario_p,
		ds_obs_recoleta = ds_observacao_p,
		nr_seq_lab	= CASE WHEN ie_amostra_recoleta_w='N' THEN nr_seq_lab  ELSE null END
	where 	nr_prescricao	=  nr_prescricao_p
	and 	nr_sequencia  	= nr_seq_prescr_p;
	commit;

elsif (ie_coleta_exame_p = 'N') then

	update 	prescr_procedimento
	set 	nr_seq_recoleta = nr_seq_motivo_p,
		nm_usuario 	= nm_usuario_p,
		ds_obs_recoleta = ds_observacao_p,
		nr_seq_lab	= CASE WHEN ie_amostra_recoleta_w='N' THEN nr_seq_lab  ELSE null END
	where 	nr_prescricao 	=  nr_prescricao_p
	and 	cd_material_exame  = cd_material_exame_p;
	commit;
end if;

end;
$body$
