SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_movto_banco_pend ( nr_seq_movto_trans_fin_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_transacao_w		double precision;
nr_sequencia_w		bigint;
nr_seq_conta_banco_w	bigint;
cd_estabelecimento_w	smallint;
dt_transacao_w		timestamp;
/* Projeto Multimoeda - Variáveis */

vl_transacao_estrang_w	double precision;
vl_complemento_w	double precision;
vl_cotacao_w		cotacao_moeda.vl_cotacao%type;
cd_moeda_w		integer;
ds_observacao_w	movto_trans_financ.ds_observacao%type;


BEGIN

if (nr_seq_movto_trans_fin_p IS NOT NULL AND nr_seq_movto_trans_fin_p::text <> '') then
	select	a.dt_transacao,
			a.vl_transacao,
			a.nr_seq_banco,
			a.vl_transacao_estrang,
			a.vl_cotacao,
			a.cd_moeda,
			substr(a.ds_observacao,1,3999)
	into STRICT	dt_transacao_w,
			vl_transacao_w,
			nr_seq_conta_banco_w,
			vl_transacao_estrang_w,
			vl_cotacao_w,
			cd_moeda_w,
			ds_observacao_w
	from	movto_trans_financ a
	where	nr_sequencia	= nr_seq_movto_trans_fin_p;

	begin
	select	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	banco_estabelecimento
	where	nr_sequencia	= nr_seq_conta_banco_w;
	exception
	when others then
		cd_estabelecimento_w	:= null;
	end;

	/* Projeto Multimoeda - verifica a existência de valor em moeda estrangeira, caso não exista limpa os campos correspondentes */

	if (coalesce(vl_transacao_estrang_w,0) <> 0) then
		vl_complemento_w := vl_transacao_w - vl_transacao_estrang_w;
	else
		vl_transacao_estrang_w	:= null;
		vl_complemento_w	:= null;
		vl_cotacao_w		:= null;
		cd_moeda_w		:= null;
	end if;

	select	nextval('movto_banco_pend_seq')
	into STRICT	nr_sequencia_w
	;

	insert into movto_banco_pend(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_conta_banco,
		dt_credito,
		vl_credito,
		vl_saldo,
		nr_seq_movto_trans_fin,
		cd_estabelecimento,
		vl_credito_estrang,
		vl_complemento,
		vl_cotacao,
		cd_moeda,
		ds_observacao)
	values (nr_sequencia_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_conta_banco_w,
		dt_transacao_w,
		vl_transacao_w,
		vl_transacao_w,
		nr_seq_movto_trans_fin_p,
		cd_estabelecimento_w,
		vl_transacao_estrang_w,
		vl_complemento_w,
		vl_cotacao_w,
		cd_moeda_w,
		substr(ds_observacao_w,1,3999));
end if;

/* Não pode ter commit nesta procedure */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
