CREATE OR REPLACE PROCEDURE inserir_movto_juros_multa_js ( nm_usuario_p text, nr_seq_banco_p bigint, nr_seq_trans_financ_p bigint, vl_transacao_p bigint, nr_sequencia_p bigint, cd_texto_tasy_p bigint, nr_seq_titulo_receber_p bigint, nr_seq_titulo_pagar_p bigint, ie_juros_multa_p text, dt_transacao_p timestamp default null) AS $body$
DECLARE

 
ds_texto_tasy_w	varchar(200);
		

BEGIN 
if (cd_texto_tasy_p IS NOT NULL AND cd_texto_tasy_p::text <> '') then 
	begin 
	ds_texto_tasy_w := obter_texto_dic_objeto(cd_texto_tasy_p, null, 'NR_TITULO='||nr_seq_titulo_receber_p||';NR_SEQ='||nr_sequencia_p);
 
	insert into movto_trans_financ(dt_atualizacao, 
		dt_transacao, 
		ie_conciliacao, 
		nm_usuario, 
		nr_lote_contabil, 
		nr_seq_banco, 
		nr_seq_trans_financ, 
		nr_sequencia, 
		vl_transacao, 
		ds_historico, 
		nr_seq_titulo_receber, 
		nr_seq_titulo_pagar, 
		nr_seq_movto_orig_acres) 
	values (clock_timestamp(), 
		coalesce(dt_transacao_p, clock_timestamp()), 
		'N', 
		nm_usuario_p, 
		0, 
		nr_seq_banco_p, 
		nr_seq_trans_financ_p, 
		nextval('movto_trans_financ_seq'), 
		vl_transacao_p, 
		ds_texto_tasy_w, 
		nr_seq_titulo_receber_p, 
		nr_seq_titulo_pagar_p, 
		nr_sequencia_p);
 
	if (nr_seq_titulo_receber_p IS NOT NULL AND nr_seq_titulo_receber_p::text <> '') then 
 
		update	titulo_receber_liq a 
		set	a.vl_multa	= CASE WHEN ie_juros_multa_p='M' THEN vl_transacao_p  ELSE a.vl_multa END , 
			a.vl_juros		= CASE WHEN ie_juros_multa_p='J' THEN vl_transacao_p  ELSE a.vl_juros END  
		where	a.nr_sequencia	= 
			(SELECT	max(x.nr_sequencia) 
			from	titulo_receber_liq x 
			where	x.nr_titulo	= a.nr_titulo) 
		and	a.nr_titulo	= nr_seq_titulo_receber_p;
 
	elsif (nr_seq_titulo_pagar_p IS NOT NULL AND nr_seq_titulo_pagar_p::text <> '') then 
 
		update	titulo_pagar_baixa a 
		set	a.vl_multa	= CASE WHEN ie_juros_multa_p='M' THEN vl_transacao_p  ELSE a.vl_multa END , 
			a.vl_juros		= CASE WHEN ie_juros_multa_p='J' THEN vl_transacao_p  ELSE a.vl_juros END  
		where	a.nr_sequencia	= 
			(SELECT	max(x.nr_sequencia) 
			from	titulo_pagar_baixa x 
			where	x.nr_titulo	= a.nr_titulo) 
		and	a.nr_titulo	= nr_seq_titulo_pagar_p;
	end if;
 
	end;
commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
