CREATE OR REPLACE PROCEDURE inserir_nf_prot_doc_barras ( nr_seq_protocolo_p bigint, nr_sequencia_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_item_w	protocolo_documento.nr_seq_tipo_doc_item%type;
ie_tipo_documento_w	varchar(1);
qt_registros_w		bigint;
nr_seq_item_w		protocolo_doc_item.nr_seq_item%type;


BEGIN
ie_tipo_documento_w := Obter_param_usuario(290, 106, obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_tipo_documento_w);

select	coalesce(max(a.nr_seq_item),0) +1
into STRICT	nr_seq_item_w
from	protocolo_doc_item a,
	protocolo_documento b
where	b.nr_sequencia = a.nr_sequencia
and	b.nr_sequencia = nr_seq_protocolo_p;

select	count(*)
into STRICT	qt_registros_w
from	protocolo_doc_item
where	nr_sequencia 	= nr_seq_protocolo_p
and 	nr_seq_interno 	= nr_sequencia_nf_p;

if (ie_tipo_documento_w = 'S') then
	select	nr_seq_tipo_doc_item
	into STRICT	nr_seq_tipo_item_w
	from	protocolo_documento
	where	nr_sequencia = nr_seq_protocolo_p;
end if;

if (qt_registros_w	= 0) then
	begin
	insert into protocolo_doc_item(
			dt_atualizacao,
			dt_inclusao_item,
			nm_usuario,
			nr_sequencia,
			nr_seq_item,
			nr_seq_tipo_item,
			nr_seq_interno)
		values (	clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_protocolo_p,
			coalesce(nr_seq_item_w,0),
			nr_seq_tipo_item_w,
			nr_sequencia_nf_p);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
