CREATE OR REPLACE PROCEDURE inserir_observacao_adep_cig ( nr_seq_cig_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_evento_w		bigint;
dt_atualizacao_w	timestamp := clock_timestamp();


BEGIN 
 
if (nr_seq_cig_p IS NOT NULL AND nr_seq_cig_p::text <> '') and (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	/* gerar evento observacao */
 
	select	nextval('atendimento_cig_evento_seq') 
	into STRICT	nr_seq_evento_w 
	;
 
	insert into atendimento_cig_evento( 
						nr_sequencia, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						dt_atualizacao, 
						nm_usuario, 
						nr_seq_cig, 
						dt_evento, 
						ie_evento, 
						cd_pessoa_evento, 
						ds_observacao 
						) 
					values ( 
						nr_seq_evento_w, 
						dt_atualizacao_w, 
						nm_usuario_p, 
						dt_atualizacao_w, 
						nm_usuario_p, 
						nr_seq_cig_p, 
						dt_atualizacao_w, 
						9, 
						obter_dados_usuario_opcao(nm_usuario_p,'C'), 
						ds_observacao_p 
						);
end if;
 
commit;
 
end;
$body$
