CREATE OR REPLACE PROCEDURE inserir_observacao_conta ( nr_interno_conta_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN

update 	conta_paciente
set    	ds_observacao 	= substr(CASE WHEN coalesce(ds_observacao,'X')='X' THEN ds_observacao_p  ELSE ds_observacao||' '||ds_observacao_p END ,1,255),
	nm_usuario	= nm_usuario_p
where  	nr_interno_conta = nr_interno_conta_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
