CREATE OR REPLACE PROCEDURE inserir_oft_anexo_relatorio ( dt_registro_p timestamp, nr_seq_consulta_p bigint, cd_profissional_p text, ds_anexo_p text, cd_relatorio_p bigint default null, cd_classif_relat_p text default null, nm_titulo_p text default null, nr_cirurgia_p bigint default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE

		
nr_sequencia_w			oft_anexo.nr_sequencia%type;
ds_titulo_w			relatorio.ds_titulo%type;
dt_registro_w  timestamp;


BEGIN
if (dt_registro_p IS NOT NULL AND dt_registro_p::text <> '') and (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') and (ds_anexo_p IS NOT NULL AND ds_anexo_p::text <> '') then
	begin

   if (coalesce(nr_cirurgia_p,0) > 0) then
      select   max(dt_liberacao)
      into STRICT     dt_registro_w
      from     cirurgia_descricao
      where    nr_cirurgia = nr_cirurgia_p;
   end if;
	
	if (cd_classif_relat_p = 'WATE') then
		select	max(ds_titulo)
		into STRICT	ds_titulo_w
		from	relatorio
		where	cd_relatorio = cd_relatorio_p;
	end if;
	
	select	nextval('oft_anexo_seq')
	into STRICT	nr_sequencia_w
	;
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		begin
		insert	into oft_anexo(
				nr_sequencia,
				dt_registro,
				cd_profissional,
				dt_atualizacao,
				nr_seq_consulta, 
				nm_usuario_nrec,
				dt_atualizacao_nrec, 
				nm_usuario, 
				ds_anexo,
				ie_situacao,
				dt_liberacao,
				cd_relatorio,
				cd_classif_relat,
				nm_titulo,
				nr_cirurgia)
		values (nr_sequencia_w,
				coalesce(dt_registro_w,dt_registro_p),
				cd_profissional_p, 
				clock_timestamp(),
				nr_seq_consulta_p,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_anexo_p,
				'A',
				clock_timestamp(),
				cd_relatorio_p,
				cd_classif_relat_p,
				coalesce(nm_titulo_p, ds_titulo_w),
				nr_cirurgia_p);
		end;
		
	end if;
	end;
end if;
commit;
end;
$body$
