CREATE OR REPLACE PROCEDURE inserir_operacao_cont_pront (nr_prontuario_p bigint, ie_tipo_operacao_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w			varchar(4000);
ds_procedimento_w		varchar(30);
qt_itens_w			bigint;
dt_operacao_w			timestamp;

C01 CURSOR FOR
	SELECT	substr(obter_desc_prescr_proc(cd_procedimento,ie_origem_proced,nr_seq_proc_interno),1,30)
	into STRICT	ds_procedimento_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_observacao_w	:= ds_procedimento_w || '-' || ds_observacao_w;

	end;
end loop;
close C01;

dt_operacao_w	:= clock_timestamp();

select 	count(*)
into STRICT	qt_itens_w
from	prontuario_controle
where	trunc(clock_timestamp(),'mi') = trunc(dt_operacao_w,'mi')
and	nr_prontuario = nr_prontuario_p
and	ie_tipo_operacao = ie_tipo_operacao_p;

if (qt_itens_w > 0) then

	dt_operacao_w	:= dt_operacao_w + qt_itens_w/86400;

end if;

insert into prontuario_controle(nr_prontuario,
		dt_operacao,
		ie_tipo_operacao,
		cd_pessoa_fisica,
		nm_usuario,
		dt_atualizacao,
		nr_atendimento,
		dt_baixa,
		nr_prescricao,
		ds_observacao,
		cd_estabelecimento)
values (		nr_prontuario_p,
		dt_operacao_w,
		ie_tipo_operacao_p,
		cd_pessoa_fisica_p,
		nm_usuario_p,
		clock_timestamp(),
		nr_atendimento_p,
		clock_timestamp(),
		nr_prescricao_p,
		CASE WHEN nr_prescricao_p=0 THEN ''  ELSE substr(substr(ds_observacao_w,1,length(ds_observacao_w)-1),1,255) END ,
		wheb_usuario_pck.get_cd_estabelecimento);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
