CREATE OR REPLACE PROCEDURE inserir_opme_proc_agenda_exame ( nr_seq_agenda_p bigint, nr_seq_proc_interno_p bigint, cd_convenio_p bigint, cd_medico_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_material_w			bigint;
qt_material_w			double precision;
ie_padrao_w			varchar(1);
cd_cgc_w			varchar(14);
ds_observacao_w				varchar(255);
cd_especialidade_w		bigint;
nr_sequencia_w			bigint;
ie_tipo_convenio_w		smallint	:= 0;
							
c01 CURSOR FOR 
	SELECT	a.cd_material, 
		a.qt_material, 
		coalesce(a.ie_padrao,'S'), 
		a.cd_cgc, 
		a.ds_observacao 
	from	proc_interno_opme a 
	where	a.nr_seq_proc_interno	=	nr_seq_proc_interno_p 
	and	coalesce(a.cd_medico::text, '') = '' 
	and	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0) 
	and	coalesce(a.ie_tipo_convenio,ie_tipo_convenio_w)	= ie_tipo_convenio_w 
	and	((coalesce(ie_pacote,'N') = 'N') or (coalesce(ie_pacote,'N') = 'O')) 
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w)) 
	and 	not exists (	SELECT	1 
			from	agenda_pac_opme x 
			where	x.nr_seq_agenda		=	nr_seq_agenda_p 
			and	x.cd_material		=	a.cd_material 
			and	x.ie_origem_inf		=	'I') 
	and 	(((a.ie_somente_exclusivo = 'S') and not exists (	select 1 
									from	proc_interno_opme x 
									where	a.nr_seq_proc_interno   	= x.nr_seq_proc_interno 
									and	x.cd_medico			= cd_medico_p)) or (coalesce(a.ie_somente_exclusivo,'N') = 'N')) 
	and		coalesce(a.nr_seq_grupo::text, '') = ''									 
	
union
 
	select	a.cd_material, 
			a.qt_material, 
			coalesce(a.ie_padrao,'S'), 
			a.cd_cgc, 
			a.ds_observacao 
	from	proc_interno_opme a 
	where	a.nr_seq_proc_interno				= nr_seq_proc_interno_p 
	and	a.cd_medico					= cd_medico_p 
	and	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0) 
	and	coalesce(a.ie_tipo_convenio,ie_tipo_convenio_w)	= ie_tipo_convenio_w 
	and	((coalesce(ie_pacote,'N') = 'N') or (coalesce(ie_pacote,'N') = 'O')) 
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w)) 
	and 	not exists (	select	1 
			from	agenda_pac_opme x 
			where	x.nr_seq_agenda		=	nr_seq_agenda_p 
			and	x.cd_material		=	a.cd_material 
			and	x.ie_origem_inf		=	'I') 
	and		coalesce(a.nr_seq_grupo::text, '') = '' 
	
union
 
	select	a.cd_material, 
		a.qt_material, 
		coalesce(a.ie_padrao,'S'), 
		a.cd_cgc, 
		a.ds_observacao 
	from	proc_interno_opme a, 
		proc_interno_opme_grupo b 
	where	b.nr_seq_proc_interno				= nr_seq_proc_interno_p 
	and	a.nr_seq_grupo					= b.nr_sequencia 
	and	a.cd_medico					= cd_medico_p 
	and	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0) 
	and	coalesce(a.ie_tipo_convenio,ie_tipo_convenio_w)	= ie_tipo_convenio_w 
	and	((coalesce(ie_pacote,'N') = 'N') or (coalesce(ie_pacote,'N') = 'O')) 
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w)) 
	and 	not exists (	select	1 
			from	agenda_pac_opme x 
			where	x.nr_seq_agenda		=	nr_seq_agenda_p 
			and	x.cd_material		=	a.cd_material 
			and	x.ie_origem_inf		=	'I') 
	
union
 
	select	a.cd_material, 
		a.qt_material, 
		coalesce(a.ie_padrao,'S'), 
		a.cd_cgc, 
		a.ds_observacao 
	from	proc_interno_opme a, 
		proc_interno_opme_grupo b 
	where	b.nr_seq_proc_interno				= nr_seq_proc_interno_p 
	and	a.nr_Seq_grupo					= b.nr_sequencia 
	and	coalesce(a.cd_medico::text, '') = '' 
	and	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0) 
	and	coalesce(a.ie_tipo_convenio,ie_tipo_convenio_w)	= ie_tipo_convenio_w 
	and	((coalesce(ie_pacote,'N') = 'N') or (coalesce(ie_pacote,'N') = 'O')) 
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w)) 
	and 	not exists (	select	1 
			from	agenda_pac_opme x 
			where	x.nr_seq_agenda		=	nr_seq_agenda_p 
			and	x.cd_material		=	a.cd_material 
			and	x.ie_origem_inf		=	'I') 
	and 	(((a.ie_somente_exclusivo = 'S') and not exists (	select 1 
									from	proc_interno_opme x 
									where	a.nr_seq_proc_interno   	= x.nr_seq_proc_interno 
									and	x.cd_medico			= cd_medico_p)) or (coalesce(a.ie_somente_exclusivo,'N') = 'N'));	
							 

BEGIN 
 
cd_especialidade_w	:=	obter_especialidade_medico(cd_medico_p,'C');
ie_tipo_convenio_w	:= 	coalesce(obter_tipo_convenio(cd_convenio_p),0);
 
open C01;
loop 
fetch C01 into	 
	cd_material_w, 
	qt_material_w, 
	ie_padrao_w, 
	cd_cgc_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	nextval('agenda_pac_opme_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	insert into agenda_pac_opme( 
		cd_material, 
		qt_material, 
		nr_sequencia, 
		nr_seq_agenda, 
		dt_atualizacao, 
		nm_usuario, 
		nr_seq_apres, 
		ie_origem_inf, 
		ie_autorizado, 
		ie_padrao, 
		cd_cgc, 
		nr_seq_proc_interno, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ds_observacao)			 
	values ( 
		cd_material_w, 
		qt_material_w, 
		nr_sequencia_w, 
		nr_seq_agenda_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		500, 
		'I', 
		'A', 
		ie_padrao_w, 
		cd_cgc_w, 
		nr_seq_proc_interno_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		null);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
