CREATE OR REPLACE PROCEDURE inserir_pac_lista_espera_atend ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


ie_insert_w		varchar(1);


BEGIN
if (coalesce(nr_atendimento_p,0) > 0) then

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_insert_w
	from	vacina_lista_espera
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(nr_atendimento::text, '') = '';

	if (ie_insert_w = 'S') then

		insert	into vacina_lista_espera(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_inclusao_lista,
			nm_usuario_inclusao,
			cd_pessoa_fisica,
			nr_atendimento,
			ie_status)
		values (	nextval('vacina_lista_espera_seq'),
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			nr_atendimento_p,
			'A');
	else

		update	vacina_lista_espera
		set	nr_atendimento = nr_atendimento_p
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	end if;

end if;

end;
$body$
