CREATE OR REPLACE PROCEDURE inserir_pac_lista_espera (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_espera_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_convenio_w		integer;
cd_medico_w		varchar(10);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
dt_agenda_w timestamp;


BEGIN

select	cd_pessoa_fisica,
	cd_convenio,
	cd_medico,
	cd_procedimento,
	ie_origem_proced,
	to_date(to_char(dt_agenda,'dd/mm/yyyy')||' ' ||to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
into STRICT	cd_pessoa_fisica_w,
	cd_convenio_w,
	cd_medico_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	dt_agenda_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;


select	nextval('paciente_espera_seq')
into STRICT	nr_seq_espera_w
;

insert	into paciente_espera(nr_sequencia,
	nr_seq_agenda,
	cd_pessoa_fisica,
	nr_prioridade,
	dt_inclusao,
	dt_atualizacao,
	nm_usuario,
	cd_convenio,
	cd_setor_atendimento,	
	cd_unidade_basica,
	cd_unidade_compl,
	ie_clinica,
	cd_medico,
	ie_situacao,
	cd_procedimento,
	ie_origem_proced,
	dt_prevista_internacao,
	cd_tipo_acomodacao,
	ds_observacao)
values (nr_seq_espera_w,
    nr_seq_agenda_p,
	cd_pessoa_fisica_w,
	1, clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	cd_convenio_w,
	null, null,
	null, null,
	cd_medico_w,
	'A', cd_procedimento_w,
	ie_origem_proced_w,
	dt_agenda_w,
	null, null);	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
