CREATE OR REPLACE PROCEDURE inserir_parecer_medic_agenda (nr_seq_tipo_parecer_p bigint, nr_seq_parecer_p bigint, cd_pessoa_fisica_p bigint, cd_medico_p bigint, ds_motivo_consulta_p text, nm_usuario_p text) AS $body$
DECLARE


qt_reg_w 	bigint;
cd_agenda_w	agenda.cd_agenda%type;


BEGIN

select	max(nr_seq_tipo_parecer),
		max(cd_agenda)
into STRICT	qt_reg_w,
		cd_agenda_w
from 	agenda
where 	nr_seq_tipo_parecer = nr_seq_tipo_parecer_p
and 	ie_situacao = 'A';


if (qt_reg_w >= 1) then

	insert into agenda_lista_espera(nr_sequencia,
		dt_agendamento,
		dt_atualizacao_nrec,
		dt_atualizacao,
		ds_observacao,
		ie_status_espera,
		cd_medico_exec,
		cd_pessoa_fisica,
		cd_agenda,
		nr_seq_parece,
		nm_usuario_nrec,
		nm_usuario,
		nm_usuario_agenda)
	values (nextval('agenda_lista_espera_seq'),
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp(),
		ds_motivo_consulta_p,
		'A',
		cd_medico_p,
		cd_pessoa_fisica_p,
		cd_agenda_w,
		nr_seq_parecer_p,
		nm_usuario_p,
		nm_usuario_p,
		nm_usuario_p);

end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
