CREATE OR REPLACE PROCEDURE inserir_participantes_equipe (nr_seq_ata_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nm_pessoa_participante_w 	varchar(80);
ds_setor_w		 	varchar(100);
nr_seq_proj_w			bigint;

c01 CURSOR FOR 
SELECT obter_nome_pf(b.cd_pessoa_fisica), 
	b.ds_setor 
from	proj_equipe_papel b, 
	proj_equipe a 
where	a.nr_sequencia = b.nr_seq_equipe 
and	a.nr_seq_proj = nr_seq_proj_w;


BEGIN 
 
select	nr_seq_projeto 
into STRICT	nr_seq_proj_w 
from	proj_ata 
where	nr_sequencia = nr_seq_ata_p;
 
open c01;
loop 
fetch c01 into	 
	nm_pessoa_participante_w, 
	ds_setor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	if (nm_pessoa_participante_w IS NOT NULL AND nm_pessoa_participante_w::text <> '') and (ds_setor_w IS NOT NULL AND ds_setor_w::text <> '') then 
	 
		insert into proj_ata_participante( nr_sequencia, 
			nr_seq_ata, 
			nm_pessoa_participante, 
			nm_usuario, 
			ds_setor, 
			dt_atualizacao, 
			ie_faltou, 
			dt_ciente_ata) 
		values (nextval('proj_ata_participante_seq'), 
			nr_seq_ata_p, 
			nm_pessoa_participante_w, 
			nm_usuario_p, 
			ds_setor_w, 
			clock_timestamp(), 
			'N', 
			clock_timestamp());
	end if;
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
