CREATE OR REPLACE PROCEDURE inserir_part_cirurgia (cd_participante_p text, nm_participante_p text, nr_cirurgia_p bigint, ie_funcao_p text, nm_usuario_p text, nr_seq_interno_p INOUT cirurgia_participante.nr_seq_interno%type) AS $body$
DECLARE


nr_sequencia_w		cirurgia_participante.nr_sequencia%type;
				

BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from 	cirurgia_participante
	where	nr_cirurgia	= nr_cirurgia_p;

    select nextval('cirurgia_participante_seq')
    into STRICT nr_seq_interno_p 
;

	insert into cirurgia_participante(
		nr_cirurgia,
		nr_sequencia,
		ie_funcao,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_pessoa_fisica,
		nm_participante,
		nr_seq_interno,
		ie_situacao,
		dt_liberacao)
	values (
		nr_cirurgia_p,
		nr_sequencia_w,
		ie_funcao_p,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_participante_p,
		substr(nm_participante_p, 0, 60),
		nr_seq_interno_p,
		'A',
		CASE WHEN Obter_Funcao_Ativa=-3006 THEN clock_timestamp()  ELSE null END );

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
