CREATE OR REPLACE PROCEDURE inserir_pedido_exame_ageint ( nr_seq_pedido_p bigint, nr_seq_exame_p bigint, nr_seq_anterior_p bigint, ie_itens_p text, nm_usuario_p text, nr_sequencia_p INOUT bigint ) AS $body$
DECLARE

 
cd_pessoa_usuario_w	varchar(15);
cd_pessoa_fisica_w	varchar(15);
nr_sequencia_w		bigint;
nr_sequencias_ww	varchar(4000);
nr_sequencia_ww		bigint;
nr_pos_virgula_ww	bigint;
nr_seq_proc_w		bigint;				

BEGIN 
if (ie_itens_p = 'N') then 
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	pedido_exame_externo 
	where	nr_sequencia	= nr_seq_pedido_p;
	 
	select	obter_dados_usuario_opcao(nm_usuario_p,'C') 
	into STRICT	cd_pessoa_usuario_w 
	;
	 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	 
		select 	nextval('ageint_pendencia_exame_seq') 
		into STRICT	nr_sequencia_w 
		;
		 
		insert into ageint_pendencia_exame( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			cd_pessoa_fisica, 
			dt_pendencia, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_liberacao, 
			cd_profissional, 
			dt_agendado) 
		values (	nr_sequencia_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_fisica_w, 
			clock_timestamp(), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			cd_pessoa_usuario_w, 
			null);
	nr_sequencia_p := nr_sequencia_w;
	end if;	
elsif (coalesce(nr_seq_exame_p,0) > 0) and (coalesce(nr_seq_pedido_p,0) > 0) and (coalesce(nr_seq_anterior_p,0) > 0) and (ie_itens_p = 'S') then	 
	 
	select 	coalesce(max(nr_proc_interno),0) 
	into STRICT	nr_seq_proc_w 
	from	med_exame_padrao 
	where	nr_sequencia = nr_seq_exame_p;
	 
	if (coalesce(nr_seq_proc_w,0) > 0) then 
	 
		insert into ageint_pend_exame_item( 
					nr_sequencia, 
					dt_atualizacao, 
					nm_usuario, 
					nr_seq_proc_interno, 
					nr_seq_pendencia, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec) 
			values (		nextval('ageint_pend_exame_item_seq'), 
					clock_timestamp(), 
					nm_usuario_p, 
					nr_seq_proc_w, 
					nr_seq_anterior_p, 
					clock_timestamp(), 
					nm_usuario_p);
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
