CREATE OR REPLACE PROCEDURE inserir_pessoas_escala ( nr_seq_escala_p bigint, cd_especialidade_p bigint) AS $body$
DECLARE

 
 
nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
qt_pendente_w		bigint;
cd_pessoa_fisica_ww	varchar(10);
nr_sequencia_ww		bigint;

C01 CURSOR FOR 
	SELECT a.cd_pessoa_fisica 
	FROM pessoa_fisica a, medico b
LEFT OUTER JOIN medico_especialidade c ON (b.cd_pessoa_fisica = c.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica = b.cd_pessoa_fisica and b.ie_vinculo_medico = 1  and ((c.cd_especialidade = cd_especialidade_p) or (cd_especialidade_p = 0)) ORDER BY a.nm_pessoa_fisica;

 
C02 CURSOR FOR 
	SELECT a.cd_pessoa_fisica 
	FROM pessoa_fisica a, medico b
LEFT OUTER JOIN medico_especialidade c ON (b.cd_pessoa_fisica = c.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica = b.cd_pessoa_fisica and b.ie_vinculo_medico = 1  and ((c.cd_especialidade = cd_especialidade_p) or (cd_especialidade_p = 0)) ORDER BY dbms_random.value;

 

BEGIN 
 
open C01;
loop 
fetch C01 into 
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
 
	select min(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	escala_diaria 
	where	nr_seq_escala = nr_seq_escala_p 
	and	coalesce(cd_pessoa_fisica::text, '') = '' 
	and	((coalesce(ie_profis_aleatorio::text, '') = '') or (ie_profis_aleatorio = 'N'));
 
	update	escala_diaria 
	set 	cd_pessoa_fisica = cd_pessoa_fisica_w 
	where 	nr_sequencia 	 = nr_sequencia_w;
 
	end;
end loop;
close C01;
 
open C02;
loop 
fetch C02 into 
	cd_pessoa_fisica_ww;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
 
	select min(nr_sequencia) 
	into STRICT	nr_sequencia_ww 
	from	escala_diaria 
	where	nr_seq_escala = nr_seq_escala_p 
	and	coalesce(cd_pessoa_fisica::text, '') = '' 
	and	((ie_profis_aleatorio IS NOT NULL AND ie_profis_aleatorio::text <> '') or (ie_profis_aleatorio = 'S'));
 
 
	update	escala_diaria 
	set 	cd_pessoa_fisica = cd_pessoa_fisica_ww 
	where 	nr_sequencia 	 = nr_sequencia_ww;
 
	end;
end loop;
close C02;
 
 
select count(*) 
into STRICT	qt_pendente_w 
from	escala_diaria 
where	nr_seq_escala = nr_seq_escala_p 
and	coalesce(cd_pessoa_fisica::text, '') = '';
 
if (qt_pendente_w > 0) then 
	CALL inserir_pessoas_escala(nr_seq_escala_p,cd_especialidade_p);
end if;
 
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
