CREATE OR REPLACE PROCEDURE inserir_pessoa_negoc ( nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_negoc_p bigint) AS $body$
DECLARE


qt_pessoa_negoc_w	bigint := 0;
nm_pessoa_negoc_w	varchar(255);


BEGIN

select 	count(*),
	coalesce(substr(obter_usuario_pessoa(cd_pessoa_negoc_p),1,20),'X')
into STRICT 	qt_pessoa_negoc_w,
	nm_pessoa_negoc_w
from	contrato
where	(cd_pessoa_negoc IS NOT NULL AND cd_pessoa_negoc::text <> '')
and	nr_sequencia = nr_seq_contrato_p;


if (qt_pessoa_negoc_w > 0)and (nm_pessoa_negoc_w <> 'X')then


insert into	contrato_usuario_lib(	nr_sequencia,
				nr_seq_contrato,
				dt_atualizacao,
				nm_usuario,
				ie_aviso_vencimento,
				nm_usuario_lib,
				ie_permite,
				ie_aviso_revisao,
				IE_GERACAO)
		values ( 	nextval('contrato_usuario_lib_seq'),
				nr_seq_contrato_p,
				clock_timestamp(),
				nm_usuario_p,
				'S',
				nm_pessoa_negoc_w,
				'S',
				'S',
				'U');


end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
