CREATE OR REPLACE PROCEDURE inserir_planta_log_transacao ( nm_usuario_p text, nr_seq_protocolo_p bigint, nr_interno_conta_p bigint, nr_doc_conv_principal_p text, cd_cgc_login_p text, cd_senha_plamta_p text, dt_entrada_p timestamp, ds_metodo_p text, ds_mensagem_p text, ie_tipo_mensagem_p text) AS $body$
DECLARE


qt_caracteres_lidos_w	bigint;
qt_caracteres_total_w	bigint;


BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (ds_metodo_p IS NOT NULL AND ds_metodo_p::text <> '') then
	begin

	qt_caracteres_lidos_w := 0;
	qt_caracteres_total_w := octet_length(ds_mensagem_p);

	while(qt_caracteres_lidos_w < qt_caracteres_total_w)
	loop
		insert
		into plamta_log_transacao(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_protocolo,
				nr_interno_conta,
				nr_doc_conv_principal,
				cd_cgc_login,
				cd_senha_plamta,
				dt_entrada,
				ds_metodo,
				ds_mensagem,
				ie_tipo_mensagem
			)
			values (
				nextval('plamta_log_transacao_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_protocolo_p,
				nr_interno_conta_p,
				nr_doc_conv_principal_p,
				cd_cgc_login_p,
				cd_senha_plamta_p,
				dt_entrada_p,
				ds_metodo_p,
				substr(ds_mensagem_p, 4000, (qt_caracteres_lidos_w + 1)),
				ie_tipo_mensagem_p
			);
		commit;
		qt_caracteres_lidos_w := qt_caracteres_lidos_w + 4000;
	end loop;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
