CREATE OR REPLACE PROCEDURE inserir_preferencia_medico ( cd_pessoa_fisica_p text, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_acao text) AS $body$
DECLARE


nr_seq_laudo_pref_w bigint;

/* 
SIGLAS:
  IN = Insert
  DE = Delete
  ED = Edit
*/
BEGIN


if (ie_tipo_acao = 'IN') then

	SELECT	nextval('prescr_proc_laudo_pref_seq')
    INTO STRICT	nr_seq_laudo_pref_w
;

	INSERT INTO prescr_proc_laudo_pref(
          nr_sequencia,
          dt_atualizacao,
          nm_usuario,
          dt_atualizacao_nrec,
          nm_usuario_nrec,
          cd_pessoa_fisica,
          nr_prescricao,
          nr_seq_prescricao)
	VALUES (
          nr_seq_laudo_pref_w,
          clock_timestamp(),
          Obter_Usuario_Ativo(),
          clock_timestamp(),
          Obter_Usuario_Ativo(),
          cd_pessoa_fisica_p,
          nr_prescricao_p,
          nr_seq_prescricao_p);
		
end if;

if (ie_tipo_acao = 'DE') then
	
	DELETE FROM prescr_proc_laudo_pref
	WHERE nr_seq_prescricao = nr_seq_prescricao_p
	AND nr_prescricao = nr_prescricao_p;
	
end if;

if (ie_tipo_acao = 'ED') then

	UPDATE prescr_proc_laudo_pref
	SET cd_pessoa_fisica = cd_pessoa_fisica_p
	WHERE nr_seq_prescricao = nr_seq_prescricao_p
	AND nr_prescricao = nr_prescricao_p;
	
end if;

commit;
end;
