CREATE OR REPLACE PROCEDURE inserir_proced_exam_compl ( cd_pessoa_fisica_p text, nr_prescricao_p bigint, nr_seq_prescr_p bigint, lista_proced_p text, nm_usuario_p text) AS $body$
DECLARE

ds_lista_procedimento_w		varchar(1000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_prescr_exam_compl_rot_w	smallint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_proc_interno_w		bigint;

BEGIN

ds_lista_procedimento_w	:= 	lista_proced_p;

while	(ds_lista_procedimento_w IS NOT NULL AND ds_lista_procedimento_w::text <> '') loop
	begin
	tam_lista_w	:= length(ds_lista_procedimento_w);
	ie_pos_virgula_w	:= position(',' in ds_lista_procedimento_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_prescr_exam_compl_rot_w	:= (substr(ds_lista_procedimento_w, 1, (ie_pos_virgula_w - 1)))::numeric;
		ds_lista_procedimento_w	:= substr(ds_lista_procedimento_w, (ie_pos_virgula_w + 1), tam_lista_w);
	end if;

	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from 	prescr_exam_compl_rot
	where	nr_sequencia	=	nr_seq_prescr_exam_compl_rot_w;

	select 	nr_proc_interno
	into STRICT	nr_proc_interno_w
	from 	procedimento
	where 	cd_procedimento = 	cd_procedimento_w
	and     ie_origem_proced =  	ie_origem_proced_w;

	insert	into	prescr_proc_exam_compl(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_pessoa_fisica,
			cd_procedimento,
			ie_origem_proced,
			ds_observacao,
			nr_prescricao,
			nr_seq_prescr,
			nr_seq_proc_interno
			)
		values (nextval('prescr_proc_exam_compl_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			cd_pessoa_fisica_p,
			cd_procedimento_w,
			ie_origem_proced_w,
			'',
			nr_prescricao_p,
			nr_seq_prescr_p,
			nr_proc_interno_w
			);
	commit;
	end;
end loop;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
