SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_proc_pac_med_fatur ( nr_sequencia_p bigint, nr_seq_atepacu_p text, dt_procedimento_p timestamp, nm_usuario_p text, ie_relancamento_p text) AS $body$
DECLARE

 
					 
nr_atendimento_w		bigint;
dt_faturamento_w		timestamp;
cd_cnpj_exec_w			varchar(50);
nr_doc_convenio_w		varchar(20);
ie_funcao_medico_w		varchar(10);
cd_medico_w			varchar(10);
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_via_acesso_w			varchar(1);
qt_fatur_w			double precision;
dt_inicio_procedimento_w	timestamp;
dt_fim_procedimento_w		timestamp;
cd_convenio_w			bigint;
cd_categoria_w			varchar(10);
nr_seq_atepacu_w		bigint;
dt_entrada_unidade_w		timestamp;
nr_seq_proc_interno_w		bigint;
cd_setor_atendimento_w		bigint;
nr_sequencia_w			bigint;
CD_PESSOA_FISICA_w		varchar(10);
qt_reg_w				bigint;

BEGIN
begin 
select	a.nr_atendimento, 
	coalesce(coalesce(b.dt_inicio_procedimento,B.DT_PROCEDIMENTO),a.dt_faturamento), 
	a.cd_cnpj_exec, 
	a.nr_doc_convenio, 
	a.ie_funcao_medico, 
	a.cd_medico, 
	b.cd_procedimento, 
	b.ie_origem_proced, 
	b.ie_via_acesso, 
	b.qt_fatur, 
	b.dt_inicio_procedimento, 
	b.dt_fim_procedimento, 
	b.nr_seq_proc_interno 
into STRICT	nr_atendimento_w, 
	dt_faturamento_w, 
	cd_cnpj_exec_w, 
	nr_doc_convenio_w, 
	ie_funcao_medico_w, 
	cd_medico_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	ie_via_acesso_w, 
	qt_fatur_w, 
	dt_inicio_procedimento_w, 
	dt_fim_procedimento_w, 
	nr_seq_proc_interno_w 
from	pep_med_fatur a, 
	pep_med_fatur_proc b 
where	a.nr_sequencia	= b.nr_seq_fatur 
and	b.nr_sequencia	= nr_sequencia_p;
 
select	cd_convenio, 
	cd_categoria, 
	nr_seq_atepacu, 
	dt_entrada_unidade, 
	cd_setor_atendimento 
into STRICT	cd_convenio_w,	 
	cd_categoria_w, 
	nr_seq_atepacu_w, 
	dt_entrada_unidade_w, 
	cd_setor_atendimento_w 
from	atendimento_paciente_v 
where	nr_atendimento	= nr_atendimento_w;
 
exception 
	when others then 
	cd_procedimento_w	:= null;
end;
 
 
 
 
if (nr_seq_atepacu_p IS NOT NULL AND nr_seq_atepacu_p::text <> '') then 
	nr_seq_atepacu_w	:= nr_seq_atepacu_p;
	select	dt_entrada_unidade, 
		cd_setor_atendimento 
	into STRICT	dt_entrada_unidade_w, 
		cd_setor_atendimento_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno	= nr_seq_atepacu_w;
end if;
select	count(*) 
into STRICT	qt_reg_w 
from	medico 
where	cd_pessoa_fisica = cd_medico_w;
 
if (qt_reg_w	=0) then 
	CD_PESSOA_FISICA_w	:= cd_medico_w;
	cd_medico_w	:= null;
	 
end if;
 
if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') and (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') then 
	 
	select	nextval('procedimento_paciente_seq') 
	into STRICT	nr_sequencia_w 
	;
	 
	insert into procedimento_paciente(	nr_sequencia, 
							nr_atendimento, 
							cd_procedimento, 
							ie_origem_proced, 
							dt_procedimento, 
							qt_procedimento, 
							dt_atualizacao, 
							nm_usuario, 
							cd_medico_executor, 
							ie_funcao_medico, 
							cd_especialidade, 
							cd_convenio, 
							cd_categoria, 
							cd_setor_atendimento, 
							dt_inicio_procedimento, 
							dt_final_procedimento, 
							cd_cgc_prestador, 
							ie_responsavel_credito, 
							ie_via_acesso, 
							nr_seq_atepacu, 
							dt_entrada_unidade, 
							nr_seq_proc_interno, 
							nr_doc_convenio, 
							CD_PESSOA_FISICA) 
					values (	nr_sequencia_w, 
							nr_atendimento_w, 
							cd_procedimento_w, 
							ie_origem_proced_w, 
							coalesce(dt_procedimento_p,dt_faturamento_w), 
							qt_fatur_w, 
							clock_timestamp(), 
							nm_usuario_p, 
							cd_medico_w, 
							ie_funcao_medico_w, 
							obter_especialidade_medico(cd_medico_w,'C'), 
							cd_convenio_w, 
							cd_categoria_w, 
							cd_setor_atendimento_w, 
							dt_inicio_procedimento_w, 
							dt_fim_procedimento_w, 
							cd_cnpj_exec_w, 
							null, 
							ie_via_acesso_w, 
							nr_seq_atepacu_w, 
							dt_entrada_unidade_w, 
							nr_seq_proc_interno_w, 
							nr_doc_convenio_w, 
							CD_PESSOA_FISICA_w);
							 
	CALL atualiza_preco_procedimento(nr_sequencia_w,cd_convenio_w,nm_usuario_p);
	 
	if (ie_relancamento_p = 'N') then 
		update	PEP_MED_FATUR_PROC 
		set	DT_ENVIO_CONTA	= clock_timestamp() 
		where	nr_sequencia = nr_sequencia_p;
	end if;
	 
end if;
	 
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_proc_pac_med_fatur ( nr_sequencia_p bigint, nr_seq_atepacu_p text, dt_procedimento_p timestamp, nm_usuario_p text, ie_relancamento_p text) FROM PUBLIC;

