CREATE OR REPLACE PROCEDURE inserir_proc_princ_autorizacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
cd_procedimento_principal_w	bigint;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w		bigint;


BEGIN

if (coalesce(nr_sequencia_p, 0) > 0) then

	select	max(cd_procedimento_principal),
		max(ie_origem_proced),
		max(nr_seq_proc_interno)
	into STRICT	cd_procedimento_principal_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w
	from	autorizacao_convenio
	where	nr_sequencia = nr_sequencia_p;

	select	count(1)
	into STRICT	qt_existe_w
	from	procedimento_autorizado
	where	nr_sequencia_autor = nr_sequencia_p
	and	cd_procedimento = cd_procedimento_principal_w
	and	ie_origem_proced = ie_origem_proced_w;

	if (qt_existe_w = 0) then

		insert	into procedimento_autorizado(nr_sequencia,
			cd_procedimento,
			ie_origem_proced,
			nr_seq_proc_interno,
			qt_autorizada,
			qt_solicitada,
			dt_atualizacao,
			nm_usuario,
			nr_sequencia_autor)
		values (	nextval('procedimento_autorizado_seq'),
			cd_procedimento_principal_w,
			ie_origem_proced_w,
			nr_seq_proc_interno_w,
			0,
			1,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_p);

	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
