CREATE OR REPLACE PROCEDURE inserir_produtos_npt_adic ( nr_sequencia_p bigint, ds_lista_check_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_check_w	varchar(2000);
ds_item_atual_w		varchar(255);
nr_pos_virgula_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ds_lista_check_p IS NOT NULL AND ds_lista_check_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_check_w	:= ds_lista_check_p;

	while(ds_lista_check_w IS NOT NULL AND ds_lista_check_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_check_w);

		if (nr_pos_virgula_w > 0) then
			begin
			ds_item_atual_w		:= substr(ds_lista_check_w,0,nr_pos_virgula_w-1);
			ds_lista_check_w	:= substr(ds_lista_check_w,nr_pos_virgula_w+1,length(ds_lista_check_w));
			end;
		else
			begin
			ds_item_atual_w		:= ds_lista_check_w;
			ds_lista_check_w	:= null;
			end;
		end if;

		if (ds_item_atual_w IS NOT NULL AND ds_item_atual_w::text <> '') then
			begin

			CALL inserir_produtos_npt(
				nr_sequencia_p,
				ds_item_atual_w,
				nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;

end;
$body$
