SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_profissionais_escala ( nr_seq_escala_p bigint) AS $body$
DECLARE

 
			 
cd_pessoa_fisica_w	varchar(15);
hr_inicial_w		varchar(20);
hr_final_w		varchar(10);
nm_pessoa_fisica_w	varchar(255);
qt_registros_w		bigint;
nr_sequencia_w		bigint;

			 
C01 CURSOR FOR 
	SELECT	cd_pessoa_fisica,	 
		hr_inicial, 
		hr_final, 
		substr(obter_nome_pf(cd_pessoa_fisica),1,255) 
	from	escala_horario_medico 
	where	(hr_inicial IS NOT NULL AND hr_inicial::text <> '') 
	and	(hr_final IS NOT NULL AND hr_final::text <> '') 
	order by 4,2,3;

 
C02 CURSOR FOR 
	SELECT min(nr_sequencia) 
	from	escala_diaria 
	where	nr_seq_escala 			= nr_seq_escala_p 
	and	to_char(dt_inicio,'hh24:mi') 	= hr_inicial_w 
	and	to_char(dt_fim,'hh24:mi')	= hr_final_w 
	and	((coalesce(ie_profis_aleatorio::text, '') = '') or (ie_profis_aleatorio = 'N')) 
	and	coalesce(cd_pessoa_fisica::text, '') = '' 
	order by dt_inicio,dt_fim;
	

BEGIN 
open C01;
loop 
fetch C01 into	 
	cd_pessoa_fisica_w, 
	hr_inicial_w, 
	hr_final_w, 
	nm_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	open C02;
	loop 
	fetch C02 into	 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
				 
		update	escala_diaria 
		set	cd_pessoa_fisica 		= cd_pessoa_fisica_w, 
			ie_profis_aleatorio		= 'N' 
		where	to_char(dt_inicio,'hh24:mi') 	= hr_inicial_w 
		and	to_char(dt_fim,'hh24:mi')	= hr_final_w 
		and	nr_sequencia			= nr_sequencia_w 
		and	nr_seq_escala 			= nr_seq_escala_p;
		 
		end;
	end loop;
	close C02;
	 
	end;
end loop;
close C01;
 
 
select 	count(*) 
into STRICT	qt_registros_w 
from	escala_diaria a 
where	a.nr_seq_escala = nr_seq_escala_p 
and	coalesce(a.cd_pessoa_fisica::text, '') = '' 
and	exists (SELECT 	1 
		from	escala_horario_medico x 
		where	to_char(dt_inicio,'hh24:mi') 	= hr_inicial 
		and	to_char(dt_fim,'hh24:mi') 	= hr_final 
		and	a.nr_seq_escala = nr_seq_escala_p) 
and	((coalesce(a.ie_profis_aleatorio::text, '') = '') or (a.ie_profis_aleatorio = 'N'));
 
commit;
 
if (qt_registros_w > 0) then 
	CALL inserir_profissionais_escala(nr_seq_escala_p);
	end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_profissionais_escala ( nr_seq_escala_p bigint) FROM PUBLIC;

