SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_proj_recurso_item_nota ( nr_seq_nota_fiscal_p bigint, nr_seq_proj_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_nota_fiscal_w	integer;
ds_lista_itens_w		varchar(255);
ds_projeto_w		varchar(80);

/* Campos hist√≥rico */

nr_seq_historico_w		bigint;
ds_historico_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_item_nf
from	nota_fiscal_item
where	nr_sequencia = nr_seq_nota_fiscal_p
order 	by	nr_item_nf;


BEGIN

update	nota_fiscal_item
set	nr_seq_proj_rec		= nr_seq_proj_rec_p
where	nr_sequencia		= nr_seq_nota_fiscal_p;

update	titulo_pagar
set	nr_seq_proj_rec		= nr_seq_proj_rec_p
where	nr_seq_nota_fiscal	= nr_seq_nota_fiscal_p;

update	titulo_receber
set	nr_seq_proj_rec		= nr_seq_proj_rec_p
where	nr_seq_nf_saida		= nr_seq_nota_fiscal_p;

open C01;
loop
fetch C01 into
	nr_item_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_lista_itens_w::text, '') = '') then
		ds_lista_itens_w	:= nr_item_nota_fiscal_w;
	else
		ds_lista_itens_w	:= substr(ds_lista_itens_w || ',' || nr_item_nota_fiscal_w,1,255);
	end if;
	end;
end loop;
close C01;

select	ds_projeto
into STRICT	ds_projeto_w
from	projeto_recurso
where	nr_sequencia = nr_seq_proj_rec_p;

ds_historico_w := substr(wheb_mensagem_pck.get_texto(303105,'DS_LISTA_ITENS_W=' || ds_lista_itens_w ||
				';DS_PROJETO_W=' || ds_projeto_w),1,255);

select	nextval('nota_fiscal_hist_seq')
into STRICT	nr_seq_historico_w
;

insert into nota_fiscal_hist(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_nota,
			cd_evento,
			ds_historico)
	values (nr_seq_historico_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_nota_fiscal_p,
			36,
			ds_historico_w
			);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
