SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_proj_recurso_item_oc ( nr_ordem_compra_p bigint, nr_seq_proj_rec_p bigint, nr_seq_conta_bco_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_oci_w			integer;
ds_lista_itens_w			varchar(2000);
ds_historico_w			varchar(2000);
ds_projeto_w			varchar(80);
nr_seq_conta_bco_w		bigint;
ds_conta_bancaria_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_item_oci
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
order by	nr_item_oci;


BEGIN

update	ordem_compra_item
set	nr_seq_proj_rec	= nr_seq_proj_rec_p,
	nr_seq_conta_bco	= CASE WHEN nr_seq_conta_bco_p=0 THEN null  ELSE nr_seq_conta_bco_p END
where	nr_ordem_compra	= nr_ordem_compra_p;

open C01;
loop
fetch C01 into
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_lista_itens_w::text, '') = '') then
		ds_lista_itens_w	:= nr_item_oci_w;
	else
		ds_lista_itens_w	:= substr(ds_lista_itens_w || ',' || nr_item_oci_w,1,2000);
	end if;
	end;
end loop;
close C01;

select	ds_projeto
into STRICT	ds_projeto_w
from	projeto_recurso
where	nr_sequencia = nr_seq_proj_rec_p;

if (coalesce(nr_seq_conta_bco_p,0) = 0) then
	ds_historico_w := WHEB_MENSAGEM_PCK.get_texto(303105,'DS_LISTA_ITENS_W='|| DS_LISTA_ITENS_W ||';DS_PROJETO_W='|| DS_PROJETO_W);
			/*Alterado o projeto recurso dos itens (#@DS_LISTA_ITENS_W#@) para #@DS_PROJETO_W#@.*/

else
	select	ds_conta
	into STRICT	ds_conta_bancaria_w
	from	banco_estabelecimento_v
	where	nr_sequencia = nr_seq_conta_bco_p;

	ds_historico_w := WHEB_MENSAGEM_PCK.get_texto(303106,'DS_LISTA_ITENS_W='|| DS_LISTA_ITENS_W ||';DS_PROJETO_W='|| DS_PROJETO_W ||';DS_CONTA_BANCARIA_W='|| DS_CONTA_BANCARIA_W);
				/*Alterado nos itens (#@DS_LISTA_ITENS_W#@) o projeto recurso para #@DS_PROJETO_W#@ e também a conta bancária para #@DS_CONTA_BANCARIA_W#@.*/

end if;

CALL inserir_historico_ordem_compra(	nr_ordem_compra_p,
				'S',
				Wheb_mensagem_pck.get_Texto(303107), /*'Alteração no projeto recurso dos itens.',*/
				ds_historico_w,
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_proj_recurso_item_oc ( nr_ordem_compra_p bigint, nr_seq_proj_rec_p bigint, nr_seq_conta_bco_p bigint, nm_usuario_p text) FROM PUBLIC;

