CREATE OR REPLACE PROCEDURE inserir_proj_recurso_item_sc ( nr_solic_compra_p bigint, nr_seq_proj_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_solic_compra_w			integer;
ds_lista_itens_w				varchar(2000);
ds_historico_w				varchar(2000);
ds_projeto_w				varchar(80);
nr_seq_conta_bco_w			bigint;
ds_conta_bancaria_w			varchar(255);

c01 CURSOR FOR
SELECT	nr_item_solic_compra
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
order by	nr_item_solic_compra;


BEGIN

update	solic_compra_item
set	nr_seq_proj_rec	= nr_seq_proj_rec_p
where	nr_solic_compra	= nr_solic_compra_p;

open C01;
loop
fetch C01 into
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_lista_itens_w::text, '') = '') then
		ds_lista_itens_w	:= nr_item_solic_compra_w;
	else
		ds_lista_itens_w	:= substr(ds_lista_itens_w || ',' || nr_item_solic_compra_w,1,2000);
	end if;
	end;
end loop;
close C01;

select	ds_projeto
into STRICT	ds_projeto_w
from	projeto_recurso
where	nr_sequencia = nr_seq_proj_rec_p;

ds_historico_w := WHEB_MENSAGEM_PCK.get_texto(303109,'DS_LISTA_ITENS_W='|| DS_LISTA_ITENS_W ||';DS_PROJETO_W='|| DS_PROJETO_W);
		/*Alterado o projeto recurso dos itens (#@DS_LISTA_ITENS_W#@) para #@DS_PROJETO_W#@.*/

CALL gerar_historico_solic_compra(	nr_solic_compra_p,
				Wheb_mensagem_pck.get_Texto(303111), /*'Alteração no projeto recurso dos itens.',*/
				ds_historico_w,
				'S',
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
