CREATE OR REPLACE PROCEDURE inserir_proj_recurso_lic_item ( nr_seq_licitacao_p bigint, nr_seq_proj_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lic_item_w			reg_lic_item.nr_seq_lic_item%type;
ds_lista_itens_w			varchar(2000);
ds_projeto_w				projeto_recurso.ds_projeto%type;

c01 CURSOR FOR
SELECT	nr_seq_lic_item
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
order by	nr_seq_lic_item;


BEGIN

update	reg_lic_item
set	nr_seq_proj_rec	= nr_seq_proj_rec_p
where	nr_seq_licitacao	= nr_seq_licitacao_p;

open C01;
loop
fetch C01 into
	nr_seq_lic_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_lista_itens_w::text, '') = '') then
		ds_lista_itens_w	:= nr_seq_lic_item_w;
	else
		ds_lista_itens_w	:= substr(ds_lista_itens_w || ',' || nr_seq_lic_item_w,1,2000);
	end if;
	end;
end loop;
close C01;

select	ds_projeto
into STRICT	ds_projeto_w
from	projeto_recurso
where	nr_sequencia = nr_seq_proj_rec_p;


insert into reg_lic_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_historico,
	ds_observacao,
	nr_seq_licitacao)
values (	nextval('reg_lic_historico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	'ALR',
	wheb_mensagem_pck.get_texto(796859, 'DS_LISTA_ITENS_W=' || ds_lista_itens_w || ';DS_PROJETO_W=' || ds_projeto_w), --Alterado o projeto recurso dos itens (#@DS_LISTA_ITENS_W#@) para #@DS_PROJETO_W#@.
	nr_seq_licitacao_p);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
