CREATE OR REPLACE PROCEDURE inserir_recomendacoes ( ds_lista_recomendacoes_p text, nr_prescricao_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_intervalo_w				varchar(7);
nr_seq_classif_w			bigint;
ds_lista_recomendacoes_w	varchar(2000);
nr_pos_virgula_w			bigint;
cd_tipo_recomend_w			bigint;

BEGIN
if (ds_lista_recomendacoes_p IS NOT NULL AND ds_lista_recomendacoes_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	ds_lista_recomendacoes_w	:= ds_lista_recomendacoes_p;
	 
	while(ds_lista_recomendacoes_w IS NOT NULL AND ds_lista_recomendacoes_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_recomendacoes_w);
		if (nr_pos_virgula_w > 0) then 
			begin 
			cd_tipo_recomend_w	:= substr(ds_lista_recomendacoes_w,0,nr_pos_virgula_w-1);
			ds_lista_recomendacoes_w	:= substr(ds_lista_recomendacoes_w,nr_pos_virgula_w+1,length(ds_lista_recomendacoes_w));			
			end;
		else 
			begin 
			cd_tipo_recomend_w	:= (ds_lista_recomendacoes_w)::numeric;
			ds_lista_recomendacoes_w	:= null;
			end;
		end if;	
		 
		if (coalesce(cd_tipo_recomend_w,0) > 0) then 
			begin 
			select	max(cd_intervalo), 
					max(nr_seq_classif) 
			into STRICT	cd_intervalo_w, 
					nr_seq_classif_w 
			from	w_prescr_recomendacao 
			where	cd_recomendacao = cd_tipo_recomend_w;
			 
			CALL inserir_recomendacao( 
				nr_prescricao_p, 
				cd_tipo_recomend_w, 
				nr_seq_classif_w, 
				cd_intervalo_w, 
				nm_usuario_p, 
				cd_perfil_p, 
				null);
			end;
		end if;
		end;
	end loop;
	end;	
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
