CREATE OR REPLACE PROCEDURE inserir_rec_pac ( nr_seq_paciente_p bigint, ds_lista_recomend_p text, ds_dias_aplicacao_p text, nr_seq_classif_p bigint, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
cd_recomendacao_w		bigint;
ie_contador_w			bigint	:= 0;
cd_intervalo_w			varchar(10);
ds_recomendacao_w		varchar(255);


BEGIN

lista_informacao_w	:= ds_lista_recomend_p;

while 	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin
	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	if (ie_pos_virgula_w <> 0) then
		cd_recomendacao_w	:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	select	substr(ds_complemento,1,255),
		coalesce(cd_intervalo,null)
	into STRICT	ds_recomendacao_w,
		cd_intervalo_w
	from	tipo_recomendacao
	where	cd_tipo_recomendacao = cd_recomendacao_w;
	
	insert into paciente_protocolo_rec(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_paciente,
		cd_recomendacao,
		ds_recomendacao,
		ds_dias_aplicacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_classif,
		cd_intervalo)
	values (	nextval('paciente_protocolo_rec_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_paciente_p,
		cd_recomendacao_w,
		ds_recomendacao_w,
		ds_dias_aplicacao_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_classif_p,
		cd_intervalo_w);
	
	ie_contador_w	:= ie_contador_w + 1;
	
	end;
end loop;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
